mapper.insert:
insert into course_participant ( course_id, sbu_id, participant_login_name, participant_email, participant_dispaly_name ) values( #{courseId}, #{sbuId}, #{cnName}, #{email}, #{displayName}
)
select LAST_INSERT_ID() AS id

INSERT INTO course ( event_name, name, url, start_time, end_time, duration) values( #{eventName}, #{name}, #{url}, #{startTime}, #{endTime}, #{duration}
)
select LAST_INSERT_ID() AS id

insert into sbu_course ( sbu_id, course_id, duration, seats, pmds ) values( #{sbuId}, #{courseId}, #{duration}, #{seats}, #{pmds}
)
select LAST_INSERT_ID() AS id

mapper.delete:
delete from course_participant where 1=1

	and course_id = #{courseId}


	and sbu_id = #{sbuId}


	and participant_email = #{email}

mapper.select:
SELECT
id,
course_id,
sbu_id,
participant_login_name,
participant_email,
participant_dispaly_name,
attend
FROM course_participant where 1=1

	and id = #{id}


	and sbu_id = #{sbuId}


	and course_id = #{courseId}


	and participant_email = #{email}

SELECT id, sbu_id, course_id, duration, seats, pmds, assign_seats, assign_pmds, swap_seats FROM sbu_course where 1=1
and id = #{id}
and sbu_id = #{sbuId}
and course_id = #{courseId}

select c.event_name, d.logo event_logo, b.id sbuId, c.url url, c.id courseId, c.name courseName, c.start_time startTime, c.end_time endTime, c.duration duration, a.seats, a.pmds, (a.seats-a.assign_seats) remainedSeats, (a.pmds-a.assign_pmds)remainedPmds from sbu_course a left join sbu b on a.sbu_id=b.id left join course c on a.course_id=c.id left join event_logo d on c.event_name=d.event_name where 1=1
and a.sbu_id = #{id}
and c.name like CONCAT('%','${courseName}','%' )
and c.event_name=#{eventName}
and c.end_time < sysdate()
and c.start_time < TIMESTAMPADD(WEEK,2, sysdate()) and c.end_time>sysdate()
and c.start_time > TIMESTAMPADD(WEEK,2, sysdate())
order by c.start_time
limit #{startIndex},#{pageSize}

SELECT id, event_name, name, url, start_time, end_time, duration FROM course where 1=1
and id = #{id}
and event_name = #{eventName}

select t.*, b.id s_id, b.sbu_id sbu_id, c.sbu_name sbu_name, b.course_id s_course_id, b.duration s_duration, b.seats s_seats, b.pmds s_pmds, b.assign_seats s_assign_seats, b.assign_pmds s_assign_pmds from ( select a.* from course a where 1=1
and a.id = #{id}
and a.event_name = #{eventName}
limit #{startIndex},#{pageSize}
)t left join sbu_course b on t.id = b.course_id left join sbu c on b.sbu_id = c.id
