mapper.select:
select a.id, a.sbu_name, a.parent_id, c.id l_id, c.name l_name, c.email l_email, c.role l_role from sbu a left join sbu_lbps b on a.id=b.sbu_id left join lbps c on b.lbps_id=c.id where 1=1
and a.id = #{id}
and c.name=#{lbps}
and c.email=#{email}
and a.parent_id = #{parentId}
and a.tag=0

select id, name, email, role
from lbps
where 1=1
and id = #{id}
and email=#{email}

select a.id, a.sbu_name, b.course_id, b.seats, b.assign_seats from sbu a left join sbu_course b on a.id=b.sbu_id where 1=1
and b.course_id = #{courseId}
and a.id != #{sbuId} and a.parent_id=0 and a.tag=0 order by a.id

select a.id sbu_id, a.sbu_name, b.course_id, b.swap_seats from sbu a left join sbu_course b on a.id=b.sbu_id where 1=1
and b.course_id = #{courseId}
and a.id != #{sbuId} and a.parent_id=0 and b.swap_seats>0 and a.tag=0 order by a.id

select
a.id,
a.sbu_name,
#{courseId} courseId,
ifnull(b.seats,0) seats
from sbu a left join sbu_course b on a.id=b.sbu_id and b.course_id=
#{courseId}
where 1=1

and a.parent_id = #{sbuId}
and a.tag=0

select c.sbu_name, d.name course_name, b.participant_email email, b.participant_dispaly_name display_name from sbu_course a left join course_participant b on a.sbu_id= b.sbu_id and a.course_id=b.course_id left join sbu c on a.sbu_id=c.id left join course d on a.course_id=d.id where 1=1
and a.sbu_id=#{sbuId}
order by a.sbu_id,a.course_id

mapper.insert:
insert into sbu
(
sbu_name,
parent_id
)
values(
#{subName},
#{parentId}

)

	select LAST_INSERT_ID() AS id

insert into lbps
(
name,
email
)
values(
#{name},
#{email}

)

	select LAST_INSERT_ID() AS id

insert into sbu_lbps
(
sbu_id,
lbps_id
)
values(
#{sbuId},
#{lbpsId}

)

	select LAST_INSERT_ID() AS id
